events.inlineCode:
console.log('Ads Fullsreen');

try {
    ysdk.adv.showFullscreenAdv({
            callbacks: {
        onClose: function(wasShown) {
            console.log("OK");
        },
            onError: function(error) {
                console.log(error);
        }
    }
    });
} 
catch (err) {
    console.log(err);
};

console.log('Ads Rewarded');

try {
    ysdk.adv.showRewardedVideo({
        callbacks: {
            onOpen: () => {
                console.log('Video ad open.');
            },
            onRewarded: () => {
                console.log('Rewarded!');
                runtimeScene._variables.add("ads_reward_result", new gdjs.Variable({ value: "rewarded" }));
            },
            onClose: () => {
                console.log('Video ad closed.');
                runtimeScene._variables.add("ads_reward_result", new gdjs.Variable({ value: "closed" }));
            }, 
            onError: (e) => {
                runtimeScene._variables.add("ads_reward_result", new gdjs.Variable({ value: "error" }));
            }
        }
    });
} 
catch (err) {
    console.log(err);
};
